mapper.select:
SELECT id, name, type, discount_amount as discountAmount, min_amount as minAmount, coach_scope as coachScope, coach_ids as coachIds, start_time as startTime, end_time as endTime, status, created_at as createdAt, updated_at as updatedAt FROM coupon
AND name LIKE CONCAT('%', #{keyword}, '%')
AND type = #{type}
AND status = #{status}
ORDER BY id DESC LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM coupon


        AND name LIKE CONCAT('%', #{keyword}, '%')


        AND type = #{type}


        AND status = #{status}
