mapper.select:
SELECT *
FROM growth_member


        AND xiaoe_nickname LIKE CONCAT('%', #{xiaoeNickname}, '%')


        AND latest_phone = #{latestPhone}


        AND status = #{status}


        AND join_type = #{joinType}


        AND xiaoe_user_id = #{xiaoeUserId}


ORDER BY create_time DESC

    LIMIT #{offset}, #{size}

SELECT COUNT(*)
FROM growth_member


        AND xiaoe_nickname LIKE CONCAT('%', #{xiaoeNickname}, '%')


        AND latest_phone = #{latestPhone}


        AND status = #{status}


        AND join_type = #{joinType}


        AND xiaoe_user_id = #{xiaoeUserId}

SELECT *
FROM growth_member


        AND xiaoe_nickname LIKE CONCAT('%', #{xiaoeNickname}, '%')


        AND latest_phone = #{latestPhone}


        AND status = #{status}


        AND join_type = #{joinType}


        AND xiaoe_user_id = #{xiaoeUserId}


ORDER BY create_time DESC

mapper.update:
UPDATE growth_member
xiaoe_nickname = #{xiaoeNickname}, latest_phone = #{latestPhone}, phone_number = #{phoneNumber}, course_completion_count = #{courseCompletionCount}, join_date = #{joinDate}, expire_date = #{expireDate}, join_type = #{joinType}, status = #{status}, xiaoe_user_id = #{xiaoeUserId}, xiaoe_tags = #{xiaoeTags}, real_name = #{realName}, birthday = #{birthday}, gender = #{gender}, city = #{city}, email = #{email}, source_channel = #{sourceChannel}, age = #{age}, address = #{address}, wechat_name = #{wechatName}, wechat_id = #{wechatId}, last_sync_time = #{lastSyncTime}, update_time = NOW()
WHERE id = #{id}

mapper.delete:
DELETE FROM growth_member
WHERE id IN

    #{id}
