mapper.select:
SELECT * FROM consult_order


        AND status = #{status}


ORDER BY id DESC
LIMIT #{offset}, #{limit}

SELECT * FROM consult_order


        AND status = #{status}


        AND (order_no LIKE CONCAT('%', #{keyword}, '%')
             OR user_id = #{keyword}
             OR counselor_id = #{keyword})


ORDER BY id DESC
LIMIT #{offset}, #{limit}

SELECT o.id, o.order_no, o.user_id, o.counselor_id, o.consult_type, o.consult_way, o.price, o.status, o.consult_time, DATE_FORMAT(o.consult_time, '%Y-%m-%d %H:%i') as consult_date, o.created_at, CASE WHEN o.status = 0 THEN GREATEST(0, TIMESTAMPDIFF(MINUTE, NOW(), DATE_ADD(o.created_at, INTERVAL 30 MINUTE))) ELSE 0 END as payment_available_minutes, c.name as counselor_name, c.head_url as counselor_head_url, vi.name as visitor_name, 0 as consult_stage, 0 as creator_type FROM consult_order o LEFT JOIN counselor c ON o.counselor_id = c.id LEFT JOIN visitor_info vi ON o.user_id = vi.user_id
AND o.status = 0 AND o.status IN (1, 2) AND o.status IN (3, 4)
AND o.user_id = #{userId}
ORDER BY o.id DESC LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM consult_order


        AND status = #{status}


        AND (order_no LIKE CONCAT('%', #{keyword}, '%')
             OR user_id = #{keyword}
             OR counselor_id = #{keyword})

mapper.update:
UPDATE consult_order
SET status = #{status},
    payment_time = #{paymentTime}
WHERE id = #{id}
